function vidFade(){vid.classList.add("stopfade")}$(document).ready(function(){$("a").on("click",function(t){if(""!==this.hash){t.preventDefault();var e=this.hash;$("html, body").animate({scrollTop:$(e).offset().top},800,function(){window.location.hash=e})}})});var vid=document.getElementById("bgvid"),pauseButton=document.querySelector("#polina button");vid.addEventListener("ended",function(){vid.pause(),vidFade()}),pauseButton.addEventListener("click",function(){vid.classList.toggle("stopfade"),vid.paused?(vid.play(),pauseButton.innerHTML="Pause"):(vid.pause(),pauseButton.innerHTML="Paused")});